// Pair consecutive keyframes into segments for WAN-FLF2V
// N keyframes → N-1 video segments
// ENFORCES MINIMUM 4 KEYFRAMES

const input = $('When Executed by Another Workflow').first().json;
const keyframes = input.keyframes || [];
const resolution = input.resolution || '480p';
const aspect_ratio = input.aspect_ratio || 'auto';

const MIN_KEYFRAMES = 4;

if (keyframes.length < 2) {
  throw new Error('CRITICAL: Need at least 2 keyframes to create any video. Got: ' + keyframes.length);
}

if (keyframes.length < MIN_KEYFRAMES) {
  throw new Error(`Insufficient keyframes: Got ${keyframes.length}, need at least ${MIN_KEYFRAMES}. This video may have too few scene changes. Consider using a different video or manually adding keyframes.`);
}

const segments = [];
for (let i = 0; i < keyframes.length - 1; i++) {
  const startFrame = keyframes[i];
  const endFrame = keyframes[i + 1];

  segments.push({
    segment_index: i,
    start_image_url: startFrame.frame_url,
    end_image_url: endFrame.frame_url,
    prompt: startFrame.prompt || 'Smooth cinematic motion, natural movement',
    resolution: resolution,
    aspect_ratio: aspect_ratio,
    start_timestamp: startFrame.timestamp,
    end_timestamp: endFrame.timestamp
  });
}

console.log(\`Paired \${keyframes.length} keyframes into \${segments.length} video segments\`);

return segments.map(seg => ({ json: seg }));

// Check WAN-FLF2V submission results
const allItems = $input.all();
const originalInputs = $('Pair Keyframes').all();

return allItems.map((item, index) => {
  const original = originalInputs[index]?.json || {};
  const hasError = item.json.error || item.json.detail || !item.json.request_id;
  const errorMsg = item.json.error?.message || item.json.detail || item.json.message || '';

  if (hasError) {
    return {
      json: {
        status: 'error',
        segment_index: original.segment_index,
        start_image_url: original.start_image_url,
        end_image_url: original.end_image_url,
        prompt: original.prompt,
        start_timestamp: original.start_timestamp,
        end_timestamp: original.end_timestamp,
        errorMsg: errorMsg || 'WAN-FLF2V submission failed'
      }
    };
  } else {
    return {
      json: {
        status: 'submitted',
        request_id: item.json.request_id,
        status_url: item.json.status_url,
        response_url: item.json.response_url,
        segment_index: original.segment_index,
        start_image_url: original.start_image_url,
        end_image_url: original.end_image_url,
        prompt: original.prompt,
        start_timestamp: original.start_timestamp,
        end_timestamp: original.end_timestamp
      }
    };
  }
});

// Prepare successful submissions for polling
const items = $input.all();

return items.map(item => ({
  json: {
    request_id: item.json.request_id,
    status_url: item.json.status_url,
    response_url: item.json.response_url,
    segment_index: item.json.segment_index,
    start_image_url: item.json.start_image_url,
    end_image_url: item.json.end_image_url,
    start_timestamp: item.json.start_timestamp,
    end_timestamp: item.json.end_timestamp,
    status: 'IN_QUEUE',
    source: 'wan-flf2v'
  }
}));

// Update polling status
const allItems = $input.all();
const pollInputs = $('Prep for Poll').all();

return allItems.map((item, index) => {
  const original = pollInputs[index]?.json || {};
  const status = item.json.status || 'UNKNOWN';
  const errorMsg = item.json.error?.message || item.json.detail || '';

  return {
    json: {
      request_id: original.request_id,
      status_url: original.status_url,
      response_url: original.response_url,
      segment_index: original.segment_index,
      start_image_url: original.start_image_url,
      end_image_url: original.end_image_url,
      start_timestamp: original.start_timestamp,
      end_timestamp: original.end_timestamp,
      status: status,
      source: 'wan-flf2v',
      errorMsg: errorMsg
    }
  };
});

// Check task completion status
const tasks = $input.first().json.tasks;
const completed = tasks.filter(t => t.status === 'COMPLETED');
const failed = tasks.filter(t => t.status === 'FAILED');
const pending = tasks.filter(t => t.status !== 'COMPLETED' && t.status !== 'FAILED');

const allDone = pending.length === 0;

return {
  json: {
    tasks: tasks,
    allDone: allDone,
    completedCount: completed.length,
    failedCount: failed.length,
    pendingCount: pending.length,
    totalCount: tasks.length
  }
};

// Split pending tasks for retry
const tasks = $input.first().json.tasks;
const pending = tasks.filter(t => t.status !== 'COMPLETED' && t.status !== 'FAILED');
return pending.map(task => ({ json: task }));

// Split completed tasks to get results
const tasks = $input.first().json.tasks;
const completed = tasks.filter(t => t.status === 'COMPLETED');
return completed.map(task => ({ json: task }));

// Extract video URLs from WAN-FLF2V results
const allItems = $input.all();
const resultInputs = $('Split Completed').all();

return allItems.map((item, index) => {
  let videoUrl = '';
  if (item.json.video && item.json.video.url) {
    videoUrl = item.json.video.url;
  }

  const original = resultInputs[index]?.json || {};

  return {
    json: {
      segment_index: original.segment_index,
      start_image_url: original.start_image_url,
      end_image_url: original.end_image_url,
      start_timestamp: original.start_timestamp,
      end_timestamp: original.end_timestamp,
      videoUrl: videoUrl,
      source: 'wan-flf2v'
    }
  };
});

// Mark failed submissions and HALT workflow
const items = $input.all();

// Check if any items failed
if (items.length > 0) {
  const failedSegments = items.map(item => item.json.segment_index).join(', ');
  const errorMsgs = items.map(item => item.json.errorMsg || 'Unknown error').join('; ');

  // Throw error to halt the entire workflow
  throw new Error(`WAN-FLF2V generation failed after retry. Failed segments: [${failedSegments}]. Errors: ${errorMsgs}`);
}

return items;

// Format final output - halt if any failures detected
const videos = $input.first().json.videos || [];

console.log('=== WAN-FLF2V Format Final Output ===');
console.log('Received', videos.length, 'total results');

// Check for any failures
const failed = videos.filter(v => v.source === 'failed' || !v.videoUrl);
if (failed.length > 0) {
  const failedIndexes = failed.map(f => f.segment_index).join(', ');
  throw new Error(`WAN-FLF2V failed for segments: [${failedIndexes}]. Halting workflow.`);
}

// Filter successful results with videoUrl
const successful = videos.filter(v => v.videoUrl && v.source === 'wan-flf2v');

console.log('Successful:', successful.length);

// Sort by segment_index
successful.sort((a, b) => {
  const aIdx = typeof a.segment_index === 'number' ? a.segment_index : 99999;
  const bIdx = typeof b.segment_index === 'number' ? b.segment_index : 99999;
  return aIdx - bIdx;
});

console.log('Final output:', successful.length, 'videos');

return {
  json: {
    success: true,
    videos: successful,
    count: successful.length
  }
};
